mapper.insert:
    	INSERT INTO DR_BK_MN_MAP (
			CO_DIV					
			, BK_SERIAL_NO			
			, MN_CO_DIV			
			, MN_IN_DAY			
			, MN_SEQ			
			, MN_AMOUNT		
    	) values (
    		#{coDiv}	   			
    		, #{bkSerialNo}   		
    		, #{mnCoDiv}							
    		, #{mnInDay} 	
    		, #{mnSeq} 	
    		, #{mnAmount}	
    	)

mapper.update:
UPDATE DR_BK_MN_MAP
   SET MN_AMOUNT 	= #{mnAmount}
 WHERE CO_DIV 		= #{coDiv}
 AND BK_SERIAL_NO 	= #{bkSerialNo}
 AND MN_CO_DIV 		= #{mnCoDiv}
 AND MN_IN_DAY 		= #{mnInDay}
 AND MN_SEQ 		= #{mnSeq}
