mapper.insert:
/* drMsCoInfoMapper.insertDrMsCoInfo - DR_MS_CO_INFO 사업장 최초 등록 */
	INSERT INTO DR_MS_CO_INFO (
		  CO_DIV
		, MS_NUM
		, MS_REGISTER
		, MS_FIRST_PICK
		, USE_YN
		, LESSON_WEEK_CNT
		, LESSON_OUTDOOR
	)VALUES (
		  #{coDiv}
		, #{msNum}
		, 'Y'
		, #{msFirstPick}
		, 'Y'
		, 0
		, NULL
	)

mapper.update:
    /* drMsCoInfoMapper.updateFirstPickYN - 선호업장으로 체크 */
    	UPDATE DR_MS_CO_INFO
    		SET MS_FIRST_PICK = #{msFirstPick}
		 WHERE 1 = 1
		 	AND MS_NUM = #{msNum}

		 	AND CO_DIV = #{coDiv}
