mapper.select:
	SELECT IFNULL(CAST(MAX(LIST_SEQ) AS UNSIGNED), 0)+1 
	  FROM DR_VOUCHER_LIST
 	 WHERE SALE_SEQ = #{saleSeq}

		SELECT *
	      FROM DR_VOUCHER_LIST 
	     WHERE 1=1

		  AND CO_DIV = #{coDiv}


		  AND SALE_DAY = #{saleDay}


		  AND SALE_SEQ = #{saleSeq}


		  AND LIST_SEQ = #{listSeq}


		  AND VC_STATE = #{vcState}

		ORDER BY LIST_SEQ

  		 LIMIT #{limit}

mapper.insert:
    	INSERT INTO DR_VOUCHER_LIST (
			CO_DIV				
			, SALE_DAY			
			, SALE_SEQ			
			, LIST_SEQ
			, VC_ONE_AMOUNT
			, VC_ONE_NET
			, VC_ONE_VAT
			, VC_SERVICE_YN
			, VC_STATE	
			, INPUT_STAFF	
			, INPUT_DATETIME	
			, INPUT_IP	
    	) values (
    		#{coDiv}			
    		, #{saleDay}		
    		, #{saleSeq}			
    		, #{listSeq}
    		, #{vcOneAmount}
    		, #{vcOneNet}
    		, #{vcOneVat}
    		, #{vcServiceYn}
    		, #{vcState}
    		, 'APP'		
    		, now()
			, #{inputIp}					
    	)

mapper.update:
UPDATE DR_VOUCHER_LIST
   SET VC_STATE 		= #{vcState}
	 , INPUT_STAFF 		= 'APP'
	 , INPUT_DATETIME 	= now()	
	 , INPUT_IP 		= #{inputIp}
 WHERE LIST_SEQ 		= #{listSeq}
   AND SALE_SEQ 		= #{saleSeq}

UPDATE DR_VOUCHER_LIST
   SET BK_CO_DIV 		= #{bkCoDiv}
	 , BK_DAY 			= #{bkDay}
	 , BK_CONDI 		= #{bkCondi}
	 , BK_TIME 			= #{bkTime}
	 , BK_SEQ 			= #{bkSeq}
	 , VC_STATE 		= #{vcState}
	 , INPUT_STAFF 		= 'APP'
	 , INPUT_DATETIME 	= now()	
	 , INPUT_IP 		= #{inputIp}
 WHERE CO_DIV 			= #{coDiv}
   AND SALE_DAY 		= #{saleDay}
   AND SALE_SEQ 		= #{saleSeq}
   AND LIST_SEQ 		= #{listSeq}

UPDATE DR_VOUCHER_LIST
   SET VC_STATE = #{vcState}
 WHERE SALE_SEQ = #{saleSeq}
