mapper.select:
SELECT B.VC_NAME , A.VC_TO_DAY , A.VC_LIMIT_CNT , A.VC_USE_CNT , A.VC_REM_CNT , A.SALE_DAY , A.SALE_SEQ , A.VC_CD , A.VC_AMOUNT , A.VC_STATE , A.MS_NUM , A.DR_SERIAL_NO , CD_TITLE1 AS VC_STATE_NM FROM DR_VOUCHER_SALE A INNER JOIN DR_VOUCHER_CODE B ON A.VC_CD = B.VC_CD INNER JOIN CD_COMMON C ON A.VC_STATE = C.CD_CODE AND C.CD_DIVISION = '019' AND C.CD_USEYN = 'Y' WHERE 1=1
AND A.CO_DIV = #{coDiv}
AND A.MS_NUM = #{msNum}
AND A.SALE_DAY BETWEEN #{strtDt} AND #{endDt}
AND (A.VC_REM_CNT = 0 AND A.VC_STATE = 1)
AND (A.VC_REM_CNT > 0 AND A.VC_STATE = 1)
AND A.USE_YN = 'Y' ORDER BY A.VC_TO_DAY, A.VC_REM_CNT, A.SALE_SEQ
LIMIT #{limit} OFFSET #{offset}

SELECT B.VC_NAME , A.VC_TO_DAY , A.VC_LIMIT_CNT , A.VC_REM_CNT , A.SALE_DAY , A.SALE_SEQ , A.VC_CD , A.VC_AMOUNT , C.CO_NAME , C.CO_DIV , B.VC_MONTH FROM DR_VOUCHER_SALE A INNER JOIN DR_VOUCHER_CODE B ON A.VC_CD = B.VC_CD INNER JOIN CO_PLACE C ON A.CO_DIV = C.CO_DIV AND C.USE_YN = 'Y' WHERE 1=1
AND A.CO_DIV = #{coDiv}
AND A.MS_NUM = #{msNum}
AND A.USE_YN = 'Y' ORDER BY SALE_DAY DESC, SALE_SEQ DESC LIMIT 1

SELECT A.CO_DIV , SALE_SEQ , A.SALE_DAY , VC_NAME , VC_MONTH , A.VC_LIMIT_CNT , A.VC_REM_CNT , DATE_FORMAT(A.VC_TO_DAY, "%Y.%m.%d") AS VC_TO_DAY , FLOOR(A.VC_AMOUNT / A.VC_LIMIT_CNT) AS AMOUNT , B.VC_SALE , B.VC_DAY_LIMIT_CNT , A.VC_CD FROM DR_VOUCHER_SALE A INNER JOIN DR_VOUCHER_CODE B ON A.VC_CD = B.VC_CD INNER JOIN ( SELECT E.CO_DIV, E.BK_DAY, E.BAY_CONDI, E.VC_CO_DIV, E.VC_CD FROM DR_BK_TIME_VOUCHER E WHERE E.BK_DAY = #{bkDay} GROUP BY E.CO_DIV, E.BK_DAY, E.BAY_CONDI, E.VC_CO_DIV, E.VC_CD ) C ON B.CO_DIV = C.VC_CO_DIV AND B.VC_CD = C.VC_CD WHERE 1=1
AND C.CO_DIV = #{coDiv}
AND C.BAY_CONDI = #{bayCondi}
AND C.BK_TIME = #{bkTime}
AND A.MS_NUM = #{msNum}
AND (A.VC_REM_CNT > 0 AND A.VC_STATE = 1) AND A.USE_YN = 'Y' ORDER BY A.VC_TO_DAY, A.VC_REM_CNT, A.SALE_SEQ

    SELECT *
	  FROM DR_VOUCHER_SALE 
	 WHERE 1=1 

	  AND CO_DIV = #{coDiv}


	  AND SALE_DAY = #{saleDay}


	  AND SALE_SEQ = #{saleSeq}


SELECT MAX(VC_TO_DAY) AS VC_TO_DAY
  FROM DR_VOUCHER_SALE A
       	INNER JOIN DR_VOUCHER_CODE B ON A.CO_DIV = B.CO_DIV	AND A.VC_CD = B.VC_CD AND B.VC_DIVISION IN ('000', '002')
 WHERE A.CO_DIV = #{coDiv}
   AND A.MS_NUM = #{msNum}
   AND A.VC_STATE IN ('01','02')

mapper.update:
UPDATE DR_MS_CO_INFO
   SET MS_VOUCHER_DAY = #{voucherDay}
 WHERE CO_DIV 		  = #{coDiv}
   AND MS_NUM 		  = #{msNum}

