mapper.update:
UPDATE DR_VOUCHER_SALE
   SET 

	VC_REM_CNT 		= VC_REM_CNT - #{vcRemCnt} 
	, VC_USE_CNT 	= VC_USE_CNT + #{vcRemCnt} 


	 VC_REM_CNT 	= VC_REM_CNT + #{vcRemCnt} 
	, VC_USE_CNT 	= VC_USE_CNT - #{vcRemCnt} 

 WHERE CO_DIV 			= #{coDiv}
   AND SALE_DAY 		= #{saleDay}
   AND SALE_SEQ 		= #{saleSeq}

UPDATE DR_VOUCHER_SALE
   SET VC_STATE 	= #{vcState} 

   ,   USE_YN 		= #{useYn} 

 WHERE SALE_SEQ 	= #{saleSeq}
