mapper.select:
/* memberMapper.selectMember - MS_ID 또는 MS_NUM으로 회원정보 조회 (정보 수정) */ SELECT MS_NUM , MS_STATUS , MS_BK_GRANT , MS_DIVISION , MS_LEVEL , MS_LOGIN_CD , MS_LINK , MS_ID , MS_PASSWORD , MS_NAME , MS_ENAME , MS_NICKNAME , MS_IMAGENAME , MS_SEX , DATE_FORMAT(MS_BIRTH, '%Y-%m-%d') AS MS_BIRTH , MS_BIRTHYL , MS_FIRST_PHONE1 , AES_DECRYPT(UNHEX(MS_MID_PHONE1), 'YIDO') AS MS_MID_PHONE1 , MS_LAST_PHONE1 , SMS_CHK1 , MS_BLANK , MS_VOUCHER_DAY , MS_WEDDINGDAY , MS_WEDDING , AES_DECRYPT(UNHEX(MS_EMAIL), 'YIDO') AS MS_EMAIL , MS_EMAIL_YN , AES_DECRYPT(UNHEX(MS_HOMETEL), 'YIDO') AS MS_HOMETEL , MS_HOMEZIP , MS_HOMEADDR1 , MS_HOMEADDR2 , MS_POST_YN , MS_COMPNM , MS_COMPNO , AES_DECRYPT(UNHEX(MS_COMPTEL), 'YIDO') AS MS_COMPTEL , AES_DECRYPT(UNHEX(MS_COMPFAX), 'YIDO') AS MS_COMPFAX , MS_COMPZIP , MS_COMPADDR1 , MS_COMPADDR2 , MS_COMP_POST_YN , MS_COMPDEPT , MS_COMPPOSITION , MS_REGDAY , MS_ENDDAY , MS_REMARK , MS_MKT_AGREE_YN , MS_DORMANT , LOGIN_TIME , MS_DI , MS_JOB_CD , MS_AREA1 , MS_AREA2 , MS_PUSH_KEY , MS_ORDER , INPUT_STAFF , INPUT_DATETIME , INPUT_IP , UPDATE_STAFF , UPDATE_DATETIME , UPDATE_IP FROM DR_MS_MAININFO WHERE 1=1
AND MS_ID = #{userId}
AND MS_NUM = #{msNum}
AND IFNULL(NULLIF(MS_BLANK,''),'N') = 'N'

    	/* memberMapper.selectMsNum - MS_ID로 MS_NUM 조회 */
		SELECT MS_NUM
		  FROM DR_MS_MAININFO
		 WHERE MS_ID = #{msId}
		   AND IFNULL(NULLIF(MS_BLANK,''),'N') = 'N'

mapper.update:
    	/* memberMapper."updateDormant" - 휴면상태 해제 (DR_MS_MAININFO) */
    	UPDATE DR_MS_MAININFO
    		SET MS_DORMANT = 'N'  
			, UPDATE_STAFF = #{updateStaff}
			, UPDATE_DATETIME = NOW()
			, UPDATE_IP = #{updateIp}
		 WHERE MS_NUM = #{msNum}

mapper.insert:
    	/* memberMapper.insertMember - 회원정보 등록 (DR_MS_MAININFO) */

    		SELECT LPAD(CAST(DR_NEXTVAL('001','','DR_MS_MAININFO_SEQ') AS UNSIGNED),12,0) MS_NUM

    	INSERT INTO DR_MS_MAININFO (
			  MS_NUM
			, MS_STATUS
			, MS_BK_GRANT
			, MS_DIVISION
			, MS_LEVEL
			, MS_LOGIN_CD
			, MS_ID
			, MS_PASSWORD
			, MS_NAME
			, MS_SEX
			, MS_FIRST_PHONE1
			, MS_MID_PHONE1
			, MS_LAST_PHONE1
			, SMS_CHK1
			, MS_BLANK
			, MS_EMAIL
			, MS_EMAIL_YN
			, MS_REGDAY		
			, MS_MKT_AGREE_YN
			, MS_DORMANT
			, INPUT_STAFF			
			, INPUT_DATETIME
			, INPUT_IP	
		) VALUES (
			  #{msNum}
			, #{msStatus}
			, #{msBkGrant}
			, #{msDivision}
			, #{msLevel}			
			, #{msLoginCd}
			, #{msId}
			, SHA2(#{msPassword}, 256)
			, #{msName}	
			, #{msSex}		
			, #{msFirstPhone1}
			, HEX(AES_ENCRYPT(#{msMidPhone1}, 'YIDO'))
			, #{msLastPhone1}
			, #{smsChk1}			
			, #{msBlank}			
			, HEX(AES_ENCRYPT(#{msEmail}, 'YIDO'))		
			, #{msEmailYn}			
			, DATE_FORMAT(NOW(), '%Y%m%d')		
			, #{msMktAgreeYn}
			, #{msDormant}
			, #{inputStaff}
			, NOW()
			, #{ipAddr}						
		)

mapper.update:
    	/* memberMapper.updateMember - 회원정보 수정 (DR_MS_MAININFO) */
    	UPDATE DR_MS_MAININFO
    		SET MS_NUM = #{msNum}

			, MS_STATUS = #{msStatus}


			, MS_PASSWORD = SHA2(#{newMsPassword}, 256)


			, MS_NAME = #{msName}


			, SMS_CHK1 = #{smsChk1}


			, MS_FIRST_PHONE1 = #{msFirstPhone1}
			, MS_MID_PHONE1 = HEX(AES_ENCRYPT(#{msMidPhone1}, 'YIDO'))
			, MS_LAST_PHONE1 = #{msLastPhone1}


			, MS_BIRTH = DATE_FORMAT(#{msBirth}, '%Y%m%d')


			, MS_COMPNM = #{msCompnm}


			, MS_JOB_CD = #{msJobCd}


			, MS_AREA1 = #{msArea1}


			, MS_AREA2 = #{msArea2}

			, UPDATE_STAFF = #{updateStaff}
			, UPDATE_DATETIME = NOW()                                                                                                                                                                                                                                                                                                                                                                                                            
			, UPDATE_IP = #{updateIp}
		 WHERE MS_NUM = #{msNum}

