mapper.insert:
    	/* proMapper.insertProNotice 프로 특이사항 등록 */
    	INSERT INTO PRO_NOTICE (
			  MS_NUM				
			, NOTICE_DIV			
			, PRO_MUST_YN			
			, PRO_REMARK
			, NOTICE_OPEN_YN	
    	) values (
    		  #{msNum}			
    		, #{noticeDiv}		
    		, 'N'		
    		, #{proRemark}
    		, #{noticeOpenYn}		
    	)

mapper.update:
   		/* proMapper.updateProNotice 프로 특이사항 수정 */
		UPDATE PRO_NOTICE
		   SET MS_NUM = #{msNum}
		   	 , PRO_MUST_YN 		= 'N'

		     , PRO_REMARK 		= #{proRemark}


		     , NOTICE_OPEN_YN 	= #{noticeOpenYn}

		 WHERE MS_NUM 			= #{msNum}
		   AND NOTICE_DIV 		= #{noticeDiv}

mapper.select:
	/* proMapper.deleteAllProLicense 프로 자격증 모두 삭제 */
	DELETE FROM PRO_LICENSE 
    WHERE 1 = 1
		AND MS_NUM = #{msNum}

mapper.insert:
	 		/* proMapper.insertProLicense 프로 자격증 등록 */

            SELECT IFNULL(MAX(LIC_SEQ), 0) + 1
				FROM PRO_LICENSE
            WHERE 1 = 1
				AND MS_NUM = #{msNum}

    	INSERT INTO PRO_LICENSE (
			  MS_NUM				
			, LIC_SEQ			
			, LIC_KIND			
			, LIC_REMARK
    	) values (
    		  #{msNum}			
    		, #{licSeq}	
    		, #{licKind}			
    		, #{licRemark}	
    	)

mapper.update:
 		/* proMapper.updateProLicense 프로 자격증 수정 */
		UPDATE PRO_LICENSE
		   SET LIC_KIND 		= #{licKind}
		     , LIC_REMARK 		= #{licRemark}
		 WHERE MS_NUM 			= #{msNum}
		   AND LIC_SEQ 			= #{licSeq}

mapper.insert:
	/* proMapper.insertProImage 프로 갤러리 이미지/영상 등록 */

            SELECT IFNULL(MAX(IMG_SEQ), 0) + 1
				FROM PRO_IMAGE
            WHERE 1 = 1
				AND MS_NUM = #{msNum}

    	INSERT INTO PRO_IMAGE (
			  MS_NUM				
			, IMG_DIV			
			, IMG_SEQ			
			, IMG_PATH
			, IMG_FILENAME
    	) values (
    		  #{msNum}			
    		, #{imgDiv}	
    		, #{imgSeq}	
    		, #{imgPath}			
    		, #{imgFilename}
    	)

mapper.delete:
/* memberMapper.deleteProImage - 프로 갤러리 이미지/영상 삭제 */
DELETE FROM PRO_IMAGE
WHERE 1 = 1
	AND IMG_SEQ = #{imgSeq} 
	AND MS_NUM = #{msNum}
